import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Footer from './footer';

function HomePage() {
  return (
    <body className="custom-font bg-custom-color flex flex-col min-h-screen">
      <Header />
      <Main />
      <div className='mt-auto'>
        <Footer />
      </div>

    </body>
  );
}

export default HomePage;

function Header() {
  return (
    <header className="text-white text-center font-bold mt-12">
      <h1>WHY.C</h1>
    </header>
  );
}

function Main() {
  const [room, setRoom] = useState("");
  const [activeRooms, setActiveRooms] = useState([]);
  const [showJoinBox, setShowJoinBox] = useState(false);
  const [showCreateBox, setShowCreateBox] = useState(false);

  const checkRoomAvailability = async () => {
    try {
      const res = await axios.get("/get_active_rooms");
      setActiveRooms(res.data);
    } catch (err) {
      alert("Error fetching active rooms.");
    }
  };

  const handlePlayClick = () => {
    setShowJoinBox(true);
  };

  const handleCreateClick = () => {
    setShowCreateBox(true);
  };

  return (
    <main className="custom-font flex flex-col items-center my-14">
      {showJoinBox ? (
        <div>
          <input type="text" placeholder="ID da Sala" />
          <Link to="/game">
            <button>ENTRAR</button>
          </Link>
        </div>
      ) : (
        <button onClick={handlePlayClick}>JOGAR</button>
      )}
      {showCreateBox ? (
        <div>
          <input type="text" placeholder="ID da Sala" />
            <Link to="/room">
              <button>CRIAR</button>
            </Link>
        </div>
      ) : (
        <button onClick={handleCreateClick}>CRIAR SALA</button>
      )}
    </main>
  );
}
